//Componente de Clase
import React, {Component} from 'react';
//Importo Axios
import axios from 'axios';

class App extends Component{

constructor(){
  //Super hereda del Componente
  super();
  //Defino el estado inicial
  this.state={
    users:[]
  }
}

//Funcion de montado
async componentDidMount(){
 //funcion asincrona, trae una promesa
  const response = await axios.get('https://jsonplaceholder.typicode.com/users')

   //Seteo el estado
  this.setState ({
    users:response.data
  })
}


//Agregar 
  addRows =() => (
    //Map itera por la cantidad de elementos que tenga en el arreglo
this.state.users.map((user)=>(
  //Se define la key
  <tr key={user.id}>
    <td>
      {user.name}
    </td>
    <td>
      {user.email}
    </td>
    <td>
      {user.website}
    </td>
  </tr>
    )) 
  );

  render (){

    return(
    <div className="margin">
      <table className="table">
        <thead>
          <tr>
            <th>
              Nombre
            </th>
            <th>
              Email
            </th>
            <th>
              Link
            </th>
          </tr>
        </thead>
        <tbody>
          {/* this se usa para hacer referencia a una funcion */}
          {this.addRows()}
        </tbody>
      </table>
    </div>
    )
  }
}

export default App;
