//Componente de Clase
import React, {Component} from 'react';

//importo el connect
import {connect} from 'react-redux'
import * as userActions from '../../redux/actions/userActions'
import Spinner from '../Spinner/Spiner'
import Fatal from '../Fatal/Fatal'

class Users extends Component{

//Funcion de montado
componentDidMount(){
this.props.getAll();
}

putContent=()=>{
  if(this.props.loading){
    return <Spinner/>
  }

  if(this.props.error){
    return <Fatal/>
  }
  return(
    <table className="table">
    <thead>
      <tr>
        <th>
          Nombre
        </th>
        <th>
          Email
        </th>
        <th>
          Link
        </th>
      </tr>
    </thead>
    <tbody>
      {/* this se usa para hacer referencia a una funcion */}
      {this. bring_Users()}
    </tbody>
  </table>
  )
}

//Agregar 
  bring_Users =() => (

    //Map itera por la cantidad de elementos que tenga en el arreglo
this.props.users.map((user)=>(
  //Se define la key
  <tr key={user.id}>
    <td>
      {user.name}
    </td>
    <td>
      {user.email}
    </td>
    <td>
      {user.website}
    </td>
  </tr>
    )) 
  );

  render (){
    return(
    <div >
     {this.putContent()}
    </div>
    )
  }
}

//Traigo los reducers que necesito
const mapStateToProps= (reducers) => {
    return reducers.usersReducer;
};
//connect recibe como primer parametro todos los reducers, y como segundo parametro todas las acciones
export default connect(mapStateToProps,userActions) (Users);
