import React, {Component} from 'react';
//Componente de Clase

class App extends Component{

constructor(){
  //Super hereda del Componente
  super();
  //Defino el estado inicial
  this.state={
    users:[
      {
      name: 'Alejandra',
      email: 'sanchezlismairy@gmail.com',
      link: 'http://lismairy-sanchez.github.io/'
      }
    ]
  }
}

//Agregar 
  addRows =() => (
    //Map itera por la cantidad de elementos que tenga en el arreglo
this.state.users.map((user)=>(
  <tr>
    <td>
      {user.name}
    </td>
    <td>
      {user.email}
    </td>
    <td>
      {user.link}
    </td>
  </tr>
    )) 
  );

  render (){
    return(
    <div className="margin">
      <table className="table">
        <thead>
          <tr>
            <th>
              Nombre
            </th>
            <th>
              Email
            </th>
            <th>
              Link
            </th>
          </tr>
        </thead>
        <tbody>
          {/* this se usa para hacer referencia a una funcion */}
          {this.addRows()}
        </tbody>
      </table>
    </div>
    )
  }
}

export default App;
