//Creo un estado inicial
const INITIAL_STATE={
    users:[]
};

//Indico que por defecto el estado sea el estado inicial
export default (state= INITIAL_STATE) =>{
    switch (action.type){
        //Traer usuarios
        case ' bring_Users':
            //Hago una copia del estado
            return {...state, users:action.payload}

            default: return state;
    }
}
