import axios from 'axios';
import {GET_ALL, LOADING, ERROR} from '../types/publicationsTypes'

export const getAll =()=>async (dispatch)=>{
    const response= await axios.get('https://jsonplaceholder.typicode.com/posts');
    dispatch({
        type:LOADING
    })
    try {
        dispatch({
            type: GET_ALL, //Tipo 
            payload:response.data 
        })
    } catch (error) {
        console.log('Error'+ error.message)
        dispatch({
            type:ERROR,
            payload:'Algo sali칩 mal, intente m치s tarde.'
        })
    }
};

export const getByUser =(key)=>async(dispatch,getState)=>{
    const {users}=getState().usersReducers;
    //const user_id=users[key].id;
    //const response= await axios.get(`https://jsonplaceholder.typicode.com/posts?userId=${user_id}`);
    const response= await axios.get('https://jsonplaceholder.typicode.com/posts?userId=1');

    dispatch({
        type: GET_ALL, //Tipo 
        payload:response.data
    })
}
